import React, { useEffect, useState } from 'react';
import { db } from '../../services/firebase';
import { collection, onSnapshot, addDoc, deleteDoc, doc } from 'firebase/firestore';
import ReactMarkdown from 'react-markdown';
import './Schedule.css';

interface ScheduleEntry {
  id: string;
  subject: string;
  day: string;
  time: string;
  room: string;
  notes?: string;
}

const DAYS = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri'];
const DAY_LABELS: Record<string, string> = {
  Mon: 'จันทร์',
  Tue: 'อังคาร',
  Wed: 'พุธ',
  Thu: 'พฤหัสบดี',
  Fri: 'ศุกร์',
};

const TIME_SLOTS = [
  '08:30-09:30',
  '09:30-10:30',
  '10:30-11:30',
  '11:30-12:30',
  '12:30-13:30',
  '13:30-14:30',
  '14:30-15:30',
  '15:30-16:30',
  '16:30-17:30',
];

const Schedule: React.FC<{ userEmail: string; isOwner: boolean }> = ({ userEmail, isOwner }) => {
  const [entries, setEntries] = useState<ScheduleEntry[]>([]);
  const [subject, setSubject] = useState('');
  const [day, setDay] = useState(DAYS[0]);
  const [time, setTime] = useState('');
  const [room, setRoom] = useState('');
  const [notes, setNotes] = useState('');

  useEffect(() => {
    const colRef = collection(db, 'schedules', userEmail, 'entries');
    const unsubscribe = onSnapshot(colRef, (snapshot) => {
      const data: ScheduleEntry[] = [];
      snapshot.forEach(docSnap => {
        data.push({ id: docSnap.id, ...docSnap.data() } as ScheduleEntry);
      });
      setEntries(data);
    });
    return () => unsubscribe();
  }, [userEmail]);

  const addEntry = async () => {
    if (subject && day && time && room) {
      const colRef = collection(db, 'schedules', userEmail, 'entries');
      await addDoc(colRef, { subject, day, time, room, notes });
      setSubject('');
      setTime('');
      setRoom('');
      setNotes('');
    }
  };

  const deleteEntry = async (id: string) => {
    const docRef = doc(db, 'schedules', userEmail, 'entries', id);
    await deleteDoc(docRef);
  };

  // ฟังก์ชันแปลงเวลาเป็นนาที
  const toMinutes = (t: string) => {
    const [h, m] = t.split(':').map(Number);
    return h * 60 + m;
  };

  // ฟังก์ชันเช็คว่า slot นี้ซ้อนกับเวลาเรียนหรือไม่
  function isTimeOverlap(slot: string, time: string) {
    const [slotStart, slotEnd] = slot.split('-');
    const [timeStart, timeEnd] = time.split('-');
    return !(toMinutes(slotEnd) <= toMinutes(timeStart) || toMinutes(slotStart) >= toMinutes(timeEnd));
  }

  // ฟังก์ชันเช็คว่า slot นี้คือจุดเริ่มต้นของ entry หรือไม่
  function isSlotStartOfEntry(slot: string, entryTime: string) {
    return slot.split('-')[0] === entryTime.split('-')[0];
  }

  // สุ่มสีพื้นหลังแต่ละวิชา (หรือจะ fix สีเองก็ได้)
  function getColor(subject: string) {
    const colors = [
      "#e3f2fd", "#fce4ec", "#e8f5e9", "#fff3e0", "#f3e5f5", "#f9fbe7", "#ede7f6"
    ];
    let hash = 0;
    for (let i = 0; i < subject.length; i++) hash += subject.charCodeAt(i);
    return colors[hash % colors.length];
  }

  return (
    <div className="schedule-container">
      <h2>Class Schedule</h2>
      {isOwner && (
        <div className="schedule-form">
          <input
            placeholder="Subject"
            value={subject}
            onChange={e => setSubject(e.target.value)}
          />
          <select value={day} onChange={e => setDay(e.target.value)}>
            {DAYS.map(d => <option key={d} value={d}>{DAY_LABELS[d]}</option>)}
          </select>
          <input
            placeholder="Time (e.g. 10:30-12:30)"
            value={time}
            onChange={e => setTime(e.target.value)}
          />
          <input
            placeholder="Room (e.g. SC2109)"
            value={room}
            onChange={e => setRoom(e.target.value)}
          />
          <input
            placeholder="หมายเหตุ (Notes, Markdown ได้)"
            value={notes}
            onChange={e => setNotes(e.target.value)}
          />
          <button onClick={addEntry}>Add</button>
        </div>
      )}
      <div className="schedule-grid-wrapper">
        <table className="schedule-grid">
          <thead>
            <tr>
              <th>วัน/เวลา</th>
              {TIME_SLOTS.map(slot => (
                <th key={slot}>{slot}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {DAYS.map(day => (
              <tr key={day}>
                <td className="time-col">{DAY_LABELS[day]}</td>
                {TIME_SLOTS.map((slot, slotIdx) => {
                  // หา entry ที่ซ้อนกับช่วงเวลานี้
                  const entry = entries.find(
                    e => e.day === day && isTimeOverlap(slot, e.time)
                  );
                  if (!entry) return <td key={day + slot} className="cell"></td>;
                  const isStart = isSlotStartOfEntry(slot, entry.time);
                  const bgColor = getColor(entry.subject);

                  return (
                    <td
                      key={day + slot}
                      className="cell"
                      style={{
                        background: bgColor,
                        borderLeft: isStart ? "4px solid #1976d2" : undefined,
                        opacity: isStart ? 1 : 0.85
                      }}
                    >
                      {isStart ? (
                        <div className="cell-content" style={{ alignItems: "flex-start" }}>
                          <div>
                            <div style={{ fontWeight: "bold", fontSize: 16, marginBottom: 2 }}>
                              <ReactMarkdown>{entry.subject}</ReactMarkdown>
                            </div>
                            <div style={{ fontSize: 13, color: '#888', marginBottom: 2 }}>
                              <ReactMarkdown>{entry.room}</ReactMarkdown>
                            </div>
                            <div style={{ fontSize: 13, color: '#555', marginBottom: 2 }}>{entry.time}</div>
                            {entry.notes && (
                              <div style={{ fontSize: 13, color: '#444', marginTop: 2 }}>
                                <ReactMarkdown>{entry.notes}</ReactMarkdown>
                              </div>
                            )}
                          </div>
                          {isOwner && (
                            <button className="delete-btn" style={{ marginTop: 6 }} onClick={() => deleteEntry(entry.id)}>ลบ</button>
                          )}
                        </div>
                      ) : null}
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Schedule;
